# =============================================================================## PHYS490 - Winter 2021 - HW2 (J. Lamarre)## =============================================================================import numpy as npclass Data():    def __init__(self,n_bits, n_training_data, n_test_data, filepath_data):                x_temp = []        y_temp = []                f = open(filepath_data, "r")        data_str = f.readlines()        for line_str in data_str:            data_temp = [int(s) for s in line_str.split(' ')]            y_temp.append(data_temp.pop(-1))            x_temp.extend(data_temp)        f.close()                x_temp = np.array(x_temp, dtype= np.float32).reshape((-1,n_bits**2))        y_temp = np.array(y_temp, dtype= np.float32).reshape((-1,1))                self.x_train = x_temp[0:n_training_data]        self.y_train = y_temp[0:n_training_data]        self.x_test = x_temp[n_training_data:]        self.y_test = y_temp[n_training_data:]